/**
 * The Class Style.
 */
public class Style {

	/** The id. */
	private String id;
    
    /** The price. */
    private String price;
    
    /** The style long name. */
    private String styleLongName;
    
    /** The trim. */
    private String trim;
    
	/**
	 * Gets the id.
	 *
	 * @return the id
	 */
	public String getId() {
		return id;
	}
	
	/**
	 * Sets the id.
	 *
	 * @param id the new id
	 */
	public void setId(String id) {
		this.id = id;
	}
	
	/**
	 * Gets the price.
	 *
	 * @return the price
	 */
	public String getPrice() {
		return price;
	}
	
	/**
	 * Sets the price.
	 *
	 * @param price the new price
	 */
	public void setPrice(String price) {
		this.price = price;
	}
	
	/**
	 * Gets the style long name.
	 *
	 * @return the style long name
	 */
	public String getStyleLongName() {
		return styleLongName;
	}
	
	/**
	 * Sets the style long name.
	 *
	 * @param styleLongName the new style long name
	 */
	public void setStyleLongName(String styleLongName) {
		this.styleLongName = styleLongName;
	}
	
	/**
	 * Gets the trim.
	 *
	 * @return the trim
	 */
	public String getTrim() {
		return trim;
	}
	
	/**
	 * Sets the trim.
	 *
	 * @param trim the new trim
	 */
	public void setTrim(String trim) {
		this.trim = trim;
	}
}
